INSERT INTO Books (title, publication_date, author, price, pages_number, original_language, binding, format_width, format_height, description, image_url, quantity)
VALUES
('Biblija', '2023-08-15', null, 30.99, 1559, null, 'Tvrdi uvez', 145, 225, 'Biblija, sveto pismo kršćanstva, predstavlja zbirku tekstova koji obuhvaćaju povijesna, moralna i duhovna učenja vjere. Sastavljena od Starog i Novog zavjeta, ova knjiga pruža dubok uvid u vjerovanja, propovijedi i životne priče koje oblikuju kršćansku tradiciju. Starozavjetni tekstovi govore o stvaranju svijeta, povijesti Izraela te prorocima, dok Novi zavjet donosi priču o Isusu Kristu, njegovom učenju, životu, smrti i uskrsnuću. Biblija je ne samo vjerski tekst, već i kulturno blago koje je oblikovalo svjetsku književnost, filozofiju i umjetnost kroz stoljeća. Ova duboko inspirativna knjiga ima trajnu vrijednost za čitatelje koji tragaju za smislom života i duhovnom povezanošću.', '/assets/images/books/bible.jpeg', 30),
('Ilijada', '2024-01-10', 1, 20.99, 415, 'Grčki', 'Tvrdi uvez', 150, 235, 'Ilijada, remek-djelo antičkog epa koje je napisao grčki pjesnik Homer, predstavlja uzbudljivo putovanje kroz epske događaje Trojanskog rata. Ova priča slijedi junačke podvige junaka poput Ahileja, Hektora, Odiseja i drugih, dok se bore za čast, ljubav i moć. Prepuna hrabrosti, sukoba bogova i dubokih ljudskih emocija, Ilijada ne samo da pruža uvid u mitologiju drevne Grčke, već i postavlja pitanja o hrabrosti, sudbini i čovječnosti. Sjajno ispričana, ova knjiga ostavlja neizbrisiv trag u književnosti, a njezina snažna priča i univerzalne teme čine je nezaobilaznim djelom za ljubitelje klasične književnosti. Osvježite svoje čitateljsko iskustvo s ovim nevjerojatnim epom koji je temelj zapadne književnosti.', '/assets/images/books/iliad.jpeg', 30),
('Odiseja', '2024-01-10', 1, 18.99, 309, 'Grčki', 'Tvrdi uvez', 145, 225, 'Odiseja, remek-djelo antičkog grčkog pjesnika Homera, pripovijeda o nevjerojatnim avanturama junaka Odiseja tijekom njegovog dugog putovanja kući nakon Trojanskog rata. Sa svojom hrabrošću, lukavstvom i izuzetnim pustolovinama, Odisej se suočava s brojnim izazovima, od susreta s mitskim bićima do borbi s bogovima. Ova epska pripovijest istražuje teme hrabrosti, vjernosti, snalažljivosti i težnje za domom. Odiseja ne samo da očarava svojim bogatim pripovjedačkim stilom i dubokim simbolizmom, već i pruža nezaboravno putovanje kroz svijet mita i čovječnosti. Za ljubitelje klasične književnosti, ova knjiga ostaje izuzetno relevantna i inspirativna, izazivajući čitatelje da razmišljaju o životu, avanturi i ljubavi prema domu. Otkrijte ljepotu i dubinu jednog od najvažnijih djela antičke književnosti uz Odiseju.', '/assets/images/books/odyssey.jpeg', 30),
('Eneida', '2024-01-28', 2, 19.99, 300, 'Latinski', 'Tvrdi uvez', 140, 220, 'Eneida je klasično djelo rimske književnosti koje je napisao Vergilije. Ova epska poema prati pustolovine junaka Eneje, trojanskog vojnog vođe, dok putuje prema Italiji, gdje će osnovati Rimsku državu. Inspirirana Homerovim epovima Ilijadom i Odisejom, Eneida istražuje teme hrabrosti, sudbine, ljubavi i vlasti. Vergilijeva vješta upotreba jezika stvorila je djelo koje je duboko ukorijenjeno u klasičnu mitologiju, a istovremeno je i veličanstvena priča koja će oduševiti ljubitelje epske književnosti. Ovo remek-djelo antike pruža čitateljima uvid u junakove izazove, borbe i putovanje prema ostvarenju njegove sudbine, čineći Eneidu nezaobilaznim čitanjem za ljubitelje klasične književnosti.', '/assets/images/books/aeneid.jpeg', 30),
('Meditacije', '2024-02-01', 3, 12.99, 210, 'Grčki', 'Meki uvez', 130, 205, 'Meditacije Marka Aurelija predstavljaju jedinstveno filozofsko djelo napisano tijekom vladavine ovog rimskog cara. Ova zbirka meditacija, iskrenih i dubokih promišljanja, pruža uvid u mudrost i introspekciju jednog od najvažnijih vođa Rimskog Carstva. Kroz ove zapise, Marko Aurelije dijeli svoje filozofske misli o etici, duhovnosti, prirodi čovjeka i univerzuma. Njegova razmišljanja nude smjernice za život ispunjen mudrošću, samoproučavanjem i razumijevanjem univerzalnih principa. Meditacije su ne samo dragocjen povijesni dokument već i vodič za duhovni rast, čineći ovu knjigu neizostavnim djelom za one koji traže inspiraciju iz antičke filozofije.', '/assets/images/books/meditations.jpeg', 30),
('Antigona', '2024-01-05', 4, 12.99, 105, 'Grčki', 'Tvrdi uvez', 130, 205, 'Antigona je klasična tragedija grčkog dramatičara Sofokla koja istražuje sukob između moralnih principa i zakona države. Glavna junakinja, Antigona, suočava se s teškom odlukom nakon što njen brat bude prokazan kao neprijatelj države. Uprkos naredbi kralja Kreona da se bratovo tijelo ostavi bez sahrane, Antigona odlučuje poslušati unutarnji glas savjesti i poštovati božanske zakone. Ova duboko emotivna drama istražuje teme hrabrosti, sukoba između pojedinca i vlasti te cijenu koju plaćamo zbog svojih uvjerenja. Sofoklova Antigona ostavlja snažan dojam i pruža čitateljima izazovnu meditaciju o moralnosti i ljudskoj sudbini. Ova klasična tragedija nudi duboke uvide u ljudsku prirodu i ostaje ključno djelo antičke grčke književnosti.', '/assets/images/books/antigone.jpeg', 30),
('Država', '2024-01-30', 5, 19.99, 345, 'Grčki', 'Tvrdi uvez', 135, 210, 'Država je monumentalno djelo antičkog grčkog filozofa Platona koje se smatra jednim od temelja političke filozofije. Kroz dijalog između likova, posebice Sokrata, Platon istražuje prirodu pravde, pravednosti i idealne države. U ovoj filozofskoj epopeji, Plato razmatra kako bi trebala izgledati idealna država te postavlja pitanja o ulozi pojedinca u društvu i odnosu između građana i vlasti. Djelo se bavi temama poput filozofske vlasti, odgoja čuvara, teorije ideja (ili oblika) te društvene pravde. Platonski dijalozi postavljaju osnove za dublje razumijevanje etičkih i društvenih pitanja, čineći Državu ključnim tekstom za sve koji su zainteresirani za političku teoriju i filozofiju. S bogatom simbolikom i dubokim filozofskim razmatranjima, Država je izazovna knjiga koja pruža čitateljima uvid u Platonsko razumijevanje društva, vlasti i ljudske prirode.', '/assets/images/books/republic.jpeg', 30),
('Ispovijesti', '2024-02-22', 6, 15.99, 340, 'Latinski', 'Tvrdi uvez', 140, 220, 'Ispovijesti autora Augustina predstavljaju duboko introspektivno putovanje kroz život jednog od najznačajnijih crkvenih otaca. U ovoj jedinstvenoj autobiografiji, Augustin otvoreno dijeli svoje unutarnje dileme, borbe i traženje smisla. Kroz iskrene ispovijesti, čitatelji ulaze u kompleksnost ljudske duše, istražujući teme vjere, grijeha, i traženja božanske milosti. Augustinova introspekcija nudi dubok uvid u ljudsku prirodu, donoseći jedinstvenu kombinaciju teoloških razmatranja i emotivne iskrenosti. Ispovijesti ostaju relevantne i inspirativne, potičući čitatelje na razmišljanje o vlastitim životima i traženju duhovnog ispunjenja.', '/assets/images/books/confessions.jpeg', 30),
('Božanstvena komedija', '2024-02-02', 7, 35.99, 853, 'Talijanski', 'Tvrdi uvez', 150, 235, 'Ovo remek-djelo Dantea Alighierija vodi čitatelja na nevjerojatno putovanje kroz tri sfere: pakao, čistilište i raj. Glavni junak, vođen duševnim vodičem, prolazi kroz različite pejzaže i susreće raznovrsne likove koji predstavljaju ljudske strasti, grijehe i vrline. Danteova vizija nudi duboke simbole i alegorije, stvarajući kompleksnu priču o duhovnom iskupljenju, božanskoj pravdi i putovanju prema istinskom duhovnom znanju. Ova epska poema oslikava ljudsku sudbinu, postavljajući pitanja o moralu, odgovornosti i vječnosti. Danteova Božanstvena komedija ostavlja trajni dojam, ne samo zbog svoje književne veličine, već i zbog svoje duboke teološke i filozofske dubine. To je nezaobilazno štivo za one koji traže putokaz kroz zamršene staze ljudskog duha.', '/assets/images/books/divinecomedy.jpeg', 30),
('Kanconijer', '2024-02-14', 8, 19.99, 453, 'Talijanski', 'Meki uvez', 135, 210, 'Ova zbirka lirskih ljubavnih pjesama autora Petrarce, poznata kao Kanconijer, predstavlja vrhunac srednjovjekovne talijanske lirike. Petrarcine stihove odlikuje duboka emotivnost, gdje se autor izražava kroz introspektivna promišljanja o ljubavi, tuzi i prolaznosti vremena. Svaka pjesma je poput dragocjenog bisera, prenoseći čitatelju intimne trenutke zaljubljenosti, čežnje i ljubavnog uzdizanja. Petrarca, majstor soneta, kroz Kanconijer stvara poetsko remek-djelo koje istražuje složene nijanse ljudskih osjećaja. Zbirka ostavlja dubok dojam svojom sposobnošću da uhvati esenciju ljubavi i čitatelje vodi kroz labirint strasti. Za ljubitelje klasične poezije, Kanconijer nudi nezaboravno putovanje kroz raskoš Petrarcinih sentimenta.', '/assets/images/books/canzoniere.jpg', 30),
('Dundo Maroje', '2024-03-01', 9, 11.99, 139, 'Hrvatski', 'Tvrdi uvez', 130, 205, 'Ovo remek-djelo hrvatskog renesansnog dramatičara Marina Držića predstavlja iznimnu komediju koja vješto kombinira humor i satiru kako bi istražila društvene norme i ljudske slabosti. Radnja prati živopisnog protagonista, Dunda Maroja, koji se suočava s različitim izazovima života u Dubrovniku 16. stoljeća. Kroz živahne dijaloge, karikature likova te humoristične situacije, Držić stvara jedinstveno kazališno iskustvo koje istovremeno zabavlja i potiče razmišljanje. Ova književna poslastica donosi ne samo zadovoljstvo čitateljima ljubiteljima klasične komedije, već i uvid u živopisni svijet renesansne Dubrovačke republike.', '/assets/images/books/dundomaroje.jpeg', 30),
('Novela od Stanca', '2024-02-25', 9, 8.99, 65, 'Hrvatski', 'Meki uvez', 125, 195, 'Uzbudljiva komična avantura Novela od Stanca autora Marina Držića pruža jedinstven uvid u svijet dubrovačkih plemića tijekom karnevalskih veselja. Mladi plemići Vlaho i Miho, uz pomoć prijatelja Dživa, smišljaju prevaru nad naivnim Stancom. Uz zamke, nesporazume i neočekivane obrate, ova šaljiva priča o prepoznavanju, prerušavanju i prevarama pruža izuzetno duhovito i zabavno kazališno iskustvo. Novela od Stanca odiše originalnošću i šarmom dubrovačke renesansne scene, čineći je neodoljivom čitateljima željnima klasične komedije.', '/assets/images/books/novelaodstanca.jpeg', 30),
('Judita', '2024-01-24', 10, 12.99, 150, 'Hrvatski', 'Tvrdi uvez', 130, 205, 'Ovo remek-djelo Marka Marulića, jednog od najvažnijih hrvatskih renesansnih književnika, predstavlja epsku pjesničku sagu koja duboko zaranja u biblijsku priču o Juditi. Kroz raskošan jezik i pjesničku maštovitost, Marulić stvara živopisni portret hrabre junakinje koja se suočava s moralnim izazovima i donosi odlučujući čin hrabrosti. Djelo donosi bogatstvo kulturne i duhovne baštine, reflektirajući humanističke ideje renesanse. Marulićeva Judita ne samo da očarava svojim pjesničkim izričajem, već i postavlja duboka pitanja o vjeri, ljubavi i snazi ženskog karaktera. Ova epska poema čini izvrsno štivo za sve koji cijene klasičnu književnost i žele uroniti u slikoviti svijet Marulićevog stvaralaštva.', '/assets/images/books/judita.jpeg', 30),
('Planine', '2024-02-08', 11, 13.49, 162, 'Hrvatski', 'Tvrdi uvez', 130, 205, 'Ova iznimna književna poslastica, prvi hrvatski roman nazvan Planine, autora Petra Zoranića, pruža nezaboravno putovanje kroz pastirsku idilu, alegoriju i duhovnu odiseju. Kroz prozu i stih, Zoranić vješto prepliće klasične motive, talijanske utjecaje te domaći pečat, stvarajući remek-djelo koje se izdvaja kao jedinstveno u hrvatskoj književnosti renesansnog razdoblja. Ovo djelo čarobno spaja klasičnu književnost s domaćim utjecajima, stvarajući prožimajuću priču koja će zasigurno oduševiti ljubitelje klasičnih književnih remek-djela. Osvježite svoju knjižničnu kolekciju ovim jedinstvenim putovanjem kroz Zoranićevu pastoralnu viziju.', '/assets/images/books/planine.jpeg', 30),
('Romeo i Julija', '2024-03-12', 12, 13.99, 159, 'Engleski', 'Tvrdi uvez', 130, 205, 'Ovaj klasik svjetske književnosti, remek-djelo Williama Shakespearea, pruža nezaboravnu priču o ljubavi, strasti i nesreći. Kroz dramu ljubavi između mladih Romea i Julije, Shakespeare stvara djelo koje je istodobno pogođeno dubokim emotivnim nabojem i suptilnim lirskim izrazom. Radnja se odvija u Veroni, gdje dvije obitelji, Montecchi i Capuleti, gaje dugu mržnju. No, ljubav između Romea i Julije, pripadnika suprotstavljenih obitelji, postaje simbol borbe protiv okrutnosti sudbine. Shakespearova spretnost u izražavanju ljudskih emocija i dubine ljubavi čini ovu tragediju neizbrisivom u svjetskoj književnosti. Ovo djelo poziva čitatelje na emocionalno putovanje kroz zamršene odnose, sukobe obitelji i snagu prave ljubavi koja će osvojiti srca ljubitelja klasične književnosti i pružiti nezaboravan doživljaj čarolije Shakespearovog pera.', '/assets/images/books/romeoandjuliet.jpeg', 30),
('San Ivanjske noći', '2024-03-12', 12, 9.99, 85, 'Engleski', 'Meki uvez', 130, 205, 'Ovo remek-djelo Williama Shakespearea, čarobna komedija San Ivanjske noći, donosi jedinstvenu mješavinu ljubavi, intriga i magije. Radnja se odvija u šumskom krajoliku tijekom jedne vrele ljetne noći, gdje se sudbine ljubavnika isprepliću na nezaboravan način. Shakespeare vješto plete priču o zapletima među ljubavnim parovima, uz dodatak čarolije vila i vilenjaka. San Ivanjske noći oduševljava svojom inventivnošću, bogatstvom likova i šarmom Shakespearovog jezika. Ovo je djelo koje će osvojiti srca ljubitelja klasične književnosti, pružajući čitateljima nezaboravan doživljaj ljubavi i magije pod zvijezdanim nebom.', '/assets/images/books/midsummernight.jpeg', 30),
('Hamlet', '2024-02-10', 12, 14.49, 189, 'Engleski', 'Tvrdi uvez', 140, 220, 'Ovo remek-djelo Williama Shakespearea, u kojem se isprepliću tragedija, tajne i moralne dileme, predstavlja jedno od najvažnijih djela svjetske književnosti. Radnja prati mladog princa u njegovoj borbi protiv korupcije, izdaje i unutarnjih sukoba. Shakespeare majstorski istražuje dubine ljudske psihe kroz lik Hamleta, čije se unutarnje previranje odražava kroz nezaboravne monologe. Priča o osveti, gubitku i moralnoj složenosti oduzima dah svojom kompleksnošću i dubinom. Ova drama pruža nezaboravan doživljaj čitateljima koji traže izazovne priče, snažne likove i razmišljanje o ljudskoj prirodi.', '/assets/images/books/hamlet.jpeg', 30),
('Kralj Lear', '2024-03-06', 12, 10.49, 115, 'Engleski', 'Meki uvez', 130, 205, 'Ovo klasično Shakespearovo djelo predstavlja epsku tragediju koja istražuje ljudsku sudbinu, vlast i obiteljske odnose. Radnja prati kralja koji, suočen s teškim odlukama, donosi pogrešne izbore, što pokreće lavinu tragedija. Djelo oduzima dah svojom emotivnom dubinom, sukobima među likovima i snažnom slikom ljudske slabosti. Shakespeare, majstor psihologije likova, stvara nezaboravne likove poput Leara, Cordelie i Edgara, čije priče ostavljaju snažan dojam na čitatelje. Ova drama je nezaobilazno štivo za ljubitelje klasične književnosti, nudeći izuzetnu meditaciju o moći, moralu i tragičnosti ljudskog iskustva.', '/assets/images/books/kinglear.jpeg', 30),
('Oluja', '2024-03-07', 12, 8.49, 89, 'Engleski', 'Meki uvez', 130, 205, 'Ova Shakespeareova drama, poznata po svojoj složenoj radnji, mitskim elementima i dubokim ljudskim emocijama, istražuje teme izdaje, pomirenja i obnove. Smještena na otoku, radnja prati likove koji se suočavaju s intrigama, magijom i osobnim transformacijama. Oluja je remek-djelo koje spaja fantastične elemente s dubokim ljudskim iskustvima. Ova drama nudi čitateljima bogato iskustvo koje istražuje ljubav, obitelj i moć. Uživajte u Shakespearovoj genijalnosti kroz ovu intrigantnu priču o preobražaju i pomirenju.', '/assets/images/books/tempest.jpeg', 30),
('Don Quijote', '2024-01-06', 13, 22.99, 862, 'Španjolski', 'Tvrdi uvez', 150, 235, 'Ovo iznimno djelo Miguela de Cervantesa prati neobične pustolovine viteza i njegova vjernog prijatelja kroz šareni svijet fantastičnih situacija. Smješten u španjolskom ruralnom krajoliku, roman istražuje sukob između stvarnosti i mašte, donoseći duhovitu satiru o viteškim idealima. S Cervantesovim vještim pripovijedanjem, čitatelji će se susresti s jedinstvenim likovima, neočekivanim preokretima i dubokim razmišljanjima o prirodi ljudske stvarnosti. Uronite u ovu klasičnu priču koja će vas nasmijati, ali i potaknuti na promišljanje o granicama između snova i stvarnosti.', '/assets/images/books/donquijote.jpeg', 30),
('Osman', '2024-02-02', 14, 13.99, 161, 'Hrvatski', 'Tvrdi uvez', 140, 220, 'Ovo epohalno djelo Ivana Gundulića, smješteno u stoljeće borbi između Osmanskog Carstva i Mletačke Republike, predstavlja dramatičnu pjesničku viziju sukoba između kršćanske i muslimanske civilizacije. Gundulićeva "Osman" otkriva sudbinu vojnog lidera Osmana II., dok istražuje teme vlasti, hrabrosti i moralnih izazova pred ratom. S bogatim jezikom i dubokim emotivnim tonovima, ovo remek-djelo ostavlja snažan dojam na čitatelje, otvarajući prozor u svijet sukoba i dilema turskog vladara.', '/assets/images/books/osman.jpeg', 30),
('Suze sina razmetnoga', '2024-03-04', 14, 8.99, 70, 'Hrvatski', 'Meki uvez', 130, 205, 'Uživajte u dubokom duhovnom putovanju kroz remek-djelo Ivana Gundulića, poemu koja prožima starozavjetnu temu iz Lukina Evanđelja. Suze sina razmetnoga donose proširenu priču o izgubljenom sinu, bogato oblikovanu kroz monologe koji istražuju dubine ljudske duše. Ova poema ne samo da produbljuje karakterizaciju glavnog lika, već se i duboko uranja u teme kajanja, oprosta i duhovnog pročišćenja. Inspirirana Biblijskom parabolom, Gundulićeva interpretacija ističe grijeh, posebno bludnost, kao ključni element koji pokreće radnju i potiče na duboka promišljanja o moralnim pitanjima. Suze sina razmetnoga obećavaju ne samo čitateljsko iskustvo, već i uvid u ljudsku prirodu i vrednote koje nadilaze vlastiti put kroz vjeru, kajanje i oprost.', '/assets/images/books/prodigalson.jpeg', 30),
('Dubravka', '2024-02-27', 14, 9.99, 95, 'Hrvatski', 'Meki uvez', 135, 215, 'Dubravka, Gundulićeva epska drama kroz dubrovačku prizmu istražuje ljubav, političke spletke i borbu za slobodu. Smještena u kontekst izmišljenog običaja posvećenog božici slobode, priča prati sudbinu mladog para, Dubravke i Miljenka, suočenog s izazovima političke korupcije. Ključni trenutak drame je neočekivani izbor ružnog bogataša Grdana umjesto očekivanog para za proslavu blagdana slobode, što rezultira nadnaravnim odgovorom boga Lera. U završnom činu, svećenik prinosi simbolične darove ljubavi i slobode, dok likovi simbolizirani društvenim manama predaju svoje poroke kao zavjetni dar u čast slobode Dubrovnika. Ova epska drama oslikava duh vremena kroz intrige, ljubavne zaplete i moralne izbore, čineći je neizostavnom čitankom za ljubitelje dubrovačke kulture i književnosti.', '/assets/images/books/dubravka.jpeg', 30),
('Patnje mladog Werthera', '2024-03-15', 15, 12.99, 140, 'Njemački', 'Meki uvez', 140, 220, 'Ovo djelo Johanna Wolfganga von Goethea prati patnje mladog Werthera, koji se zaljubljuje u Charlotte, ali je ona već zaručena za Alberta. Wertherova neuzvraćena ljubav dovodi ga do očajanja, a njegova emocionalna borba s vlastitim osjećajima postaje srž priče. Kroz osebujan stil pisanja, Goethe nas vodi kroz unutarnje nemire mladog Werthera, razmatrajući teme poput ljubavi, slobode i smisla života. Ova klasična priča o ljubavi i gubitku još uvijek dotiče srca čitatelja diljem svijeta.', '/assets/images/books/werther.jpeg', 30),
('Ana Karenjina', '2024-02-18', 16, 28.99, 864, 'Ruski', 'Tvrdi uvez', 150, 235, 'Djelo Lea Tolstoja, ovaj klasik svjetske književnosti, duboko zaranja u živote likova u carskoj Rusiji. Glavna priča prati Anu, ženu iz visokog društva koja se zaljubljuje u plemića Vronskog, što dovodi do skandala. Istovremeno, prateći i priču o njezinom bratu Stivi i njegovoj supruzi Dari, te idealističkom Levinu, roman istražuje ljubav, moralne dileme i društvene konvencije 19. stoljeća. Ova knjiga ne samo da otkriva složenost ljudske psihe, već i pruža duboke uvide u rusko društvo tog vremena.', 'assets/images/books/annakarenina.jpeg', 30),
('Rat i mir', '2024-03-21', 16, 29.99, 1120, 'Ruski', 'Tvrdi uvez', 150, 235, 'Rat i mir, epski roman Lea Tolstoja, otkriva živote više obitelji u Rusiji tijekom Napoleonovih ratova. Kroz ovu opsežnu priču, autor prodire u svijet rata i politike, ali isto tako i u intimne odnose, ljubav, obiteljske sukobe i moralna pitanja. Likovi kao što su Pjer Bezuhov, Andrej Bolkonski i Natasha Rostova, zajedno s mnoštvom drugih, prolaze kroz razne sudbine i transformacije, odražavajući širu sliku ruske društvene i vojne povijesti. Tolstojeva sposobnost da spoji individualne priče s povijesnim događajima čini ovu knjigu remek-djelom koje izaziva duboke refleksije o ljudskoj prirodi, sudbini i smislu života.', '/assets/images/books/warandpeace.jpeg', 30),
('Zločin i kazna', '2024-03-21', 17, 18.99, 562, 'Ruski', 'Meki uvez', 140, 220, 'Roman prati Raskolnikova, mladog studenta iz siromašne obitelji u Sankt Peterburgu. Pod utjecajem svojih teorija o nadmoći i moralnom pravu, Raskolnikov planira i izvršava ubojstvo starice koju smatra nevrijednom. Međutim, ubojstvo ne donosi očekivanu olakšanje, već ga uvlači u spiralu krivnje, sumnje i paranoje. Dostojevski vješto prikazuje unutarnju borbu glavnog lika, suočavajući ga s posljedicama njegovih postupaka te traženjem iskupljenja kroz duhovnu i moralnu obnovu. Kroz kompleksne likove, duboke introspekcije i slojevitu radnju, autor istražuje teme poput moralnosti, žrtve, pravde i duhovnog oslobođenja. Ova knjiga ne samo da predstavlja remek-djelo ruske književnosti, već i duboko zahvaća u ljudsku psihičku i moralnu strukturu, ostavljajući čitatelje da razmišljaju o prirodi zla i dobrote.', '/assets/images/books/crimeandpunishment.jpeg', 30),
('Braća Karamazovi', '2024-03-25', 17, 22.99, 902, 'Ruski', 'Tvrdi uvez', 150, 235, 'Roman prati kompleksne odnose između članova obitelji Karamazov - oca i tri sina - Dmitrija, Ivana i Alekseja, te njihove borbe s moralnim dilemama, duhovnim potragama i unutarnjim konfliktima. Intrigantna mješavina karaktera i događaja otkriva složene ljudske strasti, sukobe između dobra i zla te potragu za smislom i ispunjenjem u životu. Kroz različite perspektive i sudbine likova, Dostojevski istražuje teme poput religije, slobode volje, odgovornosti i ljubavi. Ova knjiga duboko zahvaća u ljudsku psihu i moralne dileme, ostavljajući čitatelje zapanjenima svojom dubinom i složenošću.', '/assets/images/books/karamazov.jpeg', 30),
('Izbor iz poezije', '2024-03-29', 18, 9.99, 50, 'Engleski', 'Meki uvez', 120, 190, 'Ova zbirka predstavlja izbor najpoznatijih Poeovih pjesama i pripovijesti, prikazujući duboku mračnu romantiku i nadrealistički stil koji je postao prepoznatljiv za autora. Poeova poezija i proza otkrivaju zamršenost ljudske psihe, istražujući teme poput ljubavi, smrti, ludila i tuge. Uključena je i čuvena pripovijest "Crni mačak", koja prati mračnu priču o gubitku razuma i moralnog propadanja glavnog lika kroz seriju neobjašnjivih i zastrašujućih događaja. Poeova izvanredna sposobnost stvaranja atmosfere napetosti i jeze čini ovu zbirku nezaobilaznim čitanjem za ljubitelje gotičke i mračne književnosti.', '/assets/images/books/edgarallanpoe.jpeg', 30),
('Posljednji Stipančići', '2024-02-01', 19, 13.99, 204, 'Hrvatski', 'Tvrdi uvez', 135, 215, 'Ova knjiga predstavlja klasično djelo hrvatske književnosti koje nas vodi kroz život i sudbinu obitelji Stipančića, s naglaskom na posljednju generaciju. Autor majstorski prikazuje borbu između tradicije i modernosti koja je obilježila obitelj Stipančić, dok se suočavaju s društvenim promjenama i novim izazovima. Kroz živopisne likove i slikovite opise, čitatelji će biti uvučeni u emotivnu i napetu priču o ljubavi, obiteljskim vezama, tajnama i gubicima. Novakova vješta pripovjedačka tehnika i duboki uvid u ljudsku psihu čine ovu knjigu neizostavnim čitanjem za sve ljubitelje hrvatske književnosti.', '/assets/images/books/stipancici.jpeg', 30),
('Gospoda Glembajevi', '2024-03-05', 20, 12.99, 132, 'Hrvatski', 'Meki uvez', 135, 215, 'Ova knjiga predstavlja remek-djelo Miroslava Krleže, koje se bavi životom i sudbinom članova obitelji Glembay, ugledne i kontroverzne plemićke obitelji u Zagrebu početkom 20. stoljeća. Kroz kompleksne likove, Krleža istražuje teme moći, obiteljskih odnosa, moralnosti i gubitka identiteta. Intrigantna drama obiteljskih tajni, skrivenih strasti i društvenih konflikata čini ovu knjigu nezaobilaznom za ljubitelje dubokoumnih književnih uradaka. Krležina izvanredna sposobnost opažanja ljudske prirode i njegova izražajna proza čine "Gospodu Glembajeve" ključnim djelom hrvatske književnosti.', '/assets/images/books/glembay.jpeg', 30),
('1984.', '2024-03-09', 21, 14.99, 296, 'Engleski', 'Meki uvez', 140, 220, 'Ova knjiga je klasični distopijski roman koji istražuje totalitarizam, nadzor i gubitak individualnih sloboda. Smještena u fiktivno društvo, radnja prati glavnog lika koji se bori protiv represivnog režima koji kontrolira svaki aspekt života građana. Autor vješto gradi mračnu atmosferu potlačenosti i straha, istovremeno postavljajući pitanja o moći, istini i slobodi. Ova knjiga je duboko provokativna i ostavlja snažan dojam na čitatelja, potičući razmišljanje o društvenim i političkim sustavima.', '/assets/images/books/nineteeneightyfour.jpeg', 30),
('Kiklop', '2024-02-09', 22, 19.99, 568, 'Hrvatski', 'Tvrdi uvez', 150, 235, 'Ova knjiga otkriva složenu i ekspresivnu sliku života u zagrebačkom boemskom miljeu prije Drugog svjetskog rata. Središnji lik, Melkior Tresić, kazališni kritičar, suočava se s unutarnjim nemirima i promjenama u društvu koje ga okružuje. Kroz njegove introspektivne misli pratimo raspadanje etičkih vrijednosti i rastući kaos u društvu. Autor, Ranko Marinković, majstorski prikazuje atmosferu i likove zagrebačke boemske scene, od bizarnih likova poput Maestra, Uga i Don Ferdinanda do fatalne Vivijane i nimfomanke Enke. Kroz groteskne i ekscentrične scene u kavanama i na ulicama, Marinković istražuje teme kao što su strah, otuđenje i moralna kriza. Ova knjiga je iznimno eruditska, prožeta citatima, mitološkim aluzijama i literarnim referencama, a istovremeno nudi slojevitu i bogatu sliku društva pred uraganom rata.', '/assets/images/books/kiklop.jpeg', 30),
('Preobražaj', '2024-04-15', 23, 10.99, 68, 'Njemački', 'Meki uvez', 120, 190, 'Ova knjiga, jedno od najpoznatijih djela Franza Kafke, prati priču Gregora Samsu, trgovca, koji se jednog jutra budi pretvoren u ogromnog kukca. Gregor se suočava s izazovima i preprekama kako bi se prilagodio svom novom obliku i situaciji. Dok se pokušava nositi s fizičkim i emocionalnim promjenama, njegova obitelj i okolina reagiraju na njegovu transformaciju na različite načine. Ova knjiga istražuje teme poput izolacije, otuđenja, obiteljskih odnosa i ljudske prirode, ostavljajući čitatelja s dubokim dojmom i mnogim pitanjima o ljudskoj egzistenciji.', '/assets/images/books/metamorphosis.jpeg', 30),
('Hobit', '2024-03-20', 24, 18.99, 232, 'Engleski', 'Tvrdi uvez', 145, 225, 'Ova avanturistička knjiga prati putovanje skromnog hobita Bilba Bagginsa, koji neočekivano postaje sudionik epske pustolovine. Bilbo se pridružuje skupini vilenjaka i čarobnjaka predvođenih moćnim čarobnjakom Gandalfom na putovanju kako bi povratili izgubljeno kraljevstvo patuljaka od strašnog zmaja Smauga. Put ih vodi kroz opasne šume, planine i doline, gdje se susreću s raznim čudovištima, vilenjacima, patuljcima i zmajevima. Na putu se Bilbo susreće sa svojim hrabrim stranama, raste kao osoba i otkriva snagu i hrabrost koje nikada nije znao da posjeduje. Ova knjiga otkriva bogatstvo Tolkienovog fantazijskog svijeta, prepunog magije, avanture i nezaboravnih likova.', '/assets/images/books/hobbit.jpeg', 30)