INSERT INTO Books (title, publication_date, author, price, pages_number, original_language, binding, format_width, format_height, description, image_url, quantity)
VALUES
('Biblija', '2023-08-15', null, 30.99, 1559, null, 'Tvrdi uvez', 145, 225, 'Biblija, sveto pismo kršćanstva, predstavlja zbirku tekstova koji obuhvaćaju povijesna, moralna i duhovna učenja vjere. Sastavljena od Starog i Novog zavjeta, ova knjiga pruža dubok uvid u vjerovanja, propovijedi i životne priče koje oblikuju kršćansku tradiciju. Starozavjetni tekstovi govore o stvaranju svijeta, povijesti Izraela te prorocima, dok Novi zavjet donosi priču o Isusu Kristu, njegovom učenju, životu, smrti i uskrsnuću. Biblija je ne samo vjerski tekst, već i kulturno blago koje je oblikovalo svjetsku književnost, filozofiju i umjetnost kroz stoljeća. Ova duboko inspirativna knjiga ima trajnu vrijednost za čitatelje koji tragaju za smislom života i duhovnom povezanošću.', '/assets/images/books/bible.jpeg', 30),
('Ilijada', '2024-01-10', 1, 20.99, 415, 'Grčki', 'Tvrdi uvez', 125, 200, 'Ilijada, remek-djelo antičkog epa koje je napisao grčki pjesnik Homer, predstavlja uzbudljivo putovanje kroz epske događaje Trojanskog rata. Ova priča slijedi junačke podvige junaka poput Ahileja, Hektora, Odiseja i drugih, dok se bore za čast, ljubav i moć. Prepuna hrabrosti, sukoba bogova i dubokih ljudskih emocija, Ilijada ne samo da pruža uvid u mitologiju drevne Grčke, već i postavlja pitanja o hrabrosti, sudbini i čovječnosti. Sjajno ispričana, ova knjiga ostavlja neizbrisiv trag u književnosti, a njezina snažna priča i univerzalne teme čine je nezaobilaznim djelom za ljubitelje klasične književnosti. Osvježite svoje čitateljsko iskustvo s ovim nevjerojatnim epom koji je temelj zapadne književnosti.', '/assets/images/books/iliad.jpeg', 30),
('Odiseja', '2024-01-10', 1, 18.99, 309, 'Grčki', 'Tvrdi uvez', 125, 200, 'Odiseja, remek-djelo antičkog grčkog pjesnika Homera, pripovijeda o nevjerojatnim avanturama junaka Odiseja tijekom njegovog dugog putovanja kući nakon Trojanskog rata. Sa svojom hrabrošću, lukavstvom i izuzetnim pustolovinama, Odisej se suočava s brojnim izazovima, od susreta s mitskim bićima do borbi s bogovima. Ova epska pripovijest istražuje teme hrabrosti, vjernosti, snalažljivosti i težnje za domom. Odiseja ne samo da očarava svojim bogatim pripovjedačkim stilom i dubokim simbolizmom, već i pruža nezaboravno putovanje kroz svijet mita i čovječnosti. Za ljubitelje klasične književnosti, ova knjiga ostaje izuzetno relevantna i inspirativna, izazivajući čitatelje da razmišljaju o životu, avanturi i ljubavi prema domu. Otkrijte ljepotu i dubinu jednog od najvažnijih djela antičke književnosti uz Odiseju.', '/assets/images/books/odyssey.jpeg', 30),
('Eneida', '2024-01-28', 2, 19.99, 300, 'Latinski', 'Tvrdi uvez', 130, 205, 'Eneida je klasično djelo rimske književnosti koje je napisao Vergilije. Ova epska poema prati pustolovine junaka Eneje, trojanskog vojnog vođe, dok putuje prema Italiji, gdje će osnovati Rimsku državu. Inspirirana Homerovim epovima Ilijadom i Odisejom, Eneida istražuje teme hrabrosti, sudbine, ljubavi i vlasti. Vergilijeva vješta upotreba jezika stvorila je djelo koje je duboko ukorijenjeno u klasičnu mitologiju, a istovremeno je i veličanstvena priča koja će oduševiti ljubitelje epske književnosti. Ovo remek-djelo antike pruža čitateljima uvid u junakove izazove, borbe i putovanje prema ostvarenju njegove sudbine, čineći Eneidu nezaobilaznim čitanjem za ljubitelje klasične književnosti.', '/assets/images/books/aeneid.jpeg', 30),
('Meditacije', '2024-02-01', 3, 12.99, 210, 'Grčki', 'Meki uvez', 110, 170, 'Meditacije Marka Aurelija predstavljaju jedinstveno filozofsko djelo napisano tijekom vladavine ovog rimskog cara. Ova zbirka meditacija, iskrenih i dubokih promišljanja, pruža uvid u mudrost i introspekciju jednog od najvažnijih vođa Rimskog Carstva. Kroz ove zapise, Marko Aurelije dijeli svoje filozofske misli o etici, duhovnosti, prirodi čovjeka i univerzuma. Njegova razmišljanja nude smjernice za život ispunjen mudrošću, samoproučavanjem i razumijevanjem univerzalnih principa. Meditacije su ne samo dragocjen povijesni dokument već i vodič za duhovni rast, čineći ovu knjigu neizostavnim djelom za one koji traže inspiraciju iz antičke filozofije.', '/assets/images/books/meditations.jpeg', 30),
('Antigona', '2024-01-05', 4, 12.99, 105, 'Grčki', 'Tvrdi uvez', 130, 205, 'Antigona je klasična tragedija grčkog dramatičara Sofokla koja istražuje sukob između moralnih principa i zakona države. Glavna junakinja, Antigona, suočava se s teškom odlukom nakon što njen brat bude prokazan kao neprijatelj države. Uprkos naredbi kralja Kreona da se bratovo tijelo ostavi bez sahrane, Antigona odlučuje poslušati unutarnji glas savjesti i poštovati božanske zakone. Ova duboko emotivna drama istražuje teme hrabrosti, sukoba između pojedinca i vlasti te cijenu koju plaćamo zbog svojih uvjerenja. Sofoklova Antigona ostavlja snažan dojam i pruža čitateljima izazovnu meditaciju o moralnosti i ljudskoj sudbini. Ova klasična tragedija nudi duboke uvide u ljudsku prirodu i ostaje ključno djelo antičke grčke književnosti.', '/assets/images/books/antigone.jpeg', 30),
('Kralj Edip', '2024-01-05', 4, 9.99, 100, 'Grčki', 'Meki uvez', 110, 170, 'Kralj Edip je remek-djelo klasične grčke tragedije, napisano od strane Sofokla. Radnja ove dramatične priče vrti se oko sudbine Edipa, kralja Tebe, koji nehotice postaje akter u vlastitoj tragediji. Sukobljen s proročanstvom koje mu predviđa užasne događaje, Edip pokušava izbjeći svoju sudbinu, ali neuspješno. Knjiga istražuje kompleksne teme sudbine, slobodne volje, istine i moralne odgovornosti. U svom traganju za istinom, Edip otkriva bolnu prošlost svoje obitelji i suočava se s vlastitim postupcima. Sofoklov jezik i dramaturgija donose duboku emotivnost, a likovi su složeni i nezaboravni. Kralj Edip nije samo priča o jednom čovjeku, već i snažna meditacija o ljudskoj sudbini i posljedicama naših odluka. Ova klasična grčka tragedija ostavlja trajan dojam na čitatelje, istražujući temeljne aspekte ljudske egzistencije.', '/assets/images/books/oedipusrex.jpeg', 30),
('Država', '2024-01-30', 5, 19.99, 345, 'Grčki', 'Tvrdi uvez', 135, 210, 'Država je monumentalno djelo antičkog grčkog filozofa Platona koje se smatra jednim od temelja političke filozofije. Kroz dijalog između likova, posebice Sokrata, Platon istražuje prirodu pravde, pravednosti i idealne države. U ovoj filozofskoj epopeji, Plato razmatra kako bi trebala izgledati idealna država te postavlja pitanja o ulozi pojedinca u društvu i odnosu između građana i vlasti. Djelo se bavi temama poput filozofske vlasti, odgoja čuvara, teorije ideja (ili oblika) te društvene pravde. Platonski dijalozi postavljaju osnove za dublje razumijevanje etičkih i društvenih pitanja, čineći Državu ključnim tekstom za sve koji su zainteresirani za političku teoriju i filozofiju. S bogatom simbolikom i dubokim filozofskim razmatranjima, Država je izazovna knjiga koja pruža čitateljima uvid u Platonsko razumijevanje društva, vlasti i ljudske prirode.', '/assets/images/books/republic.jpeg', 30),
('Obrana Sokratova', '2024-02-10', 5, 8.99, 80, 'Grčki', 'Meki uvez', 110, 170, 'Ova klasična filozofska rasprava predstavlja Sokratovu strastvenu obranu vlastite filozofije i života pred atenskim sudom. Kroz dijaloge i argumentaciju, Sokrat istražuje svoje uvjerenje da je filozofija ključna za moralno vođenje života. Ova duboka i introspektivna rasprava bavi se pitanjima etike, vjere i ljudske mudrosti te pruža čitateljima uvid u Sokratovu neustrašivost pred izazovima koje mu postavlja društvo. Obrana Sokratova ostaje snažna izjava o vrijednosti slobode mišljenja i posvećenosti istini, čineći je esencijalnim djelom za one koji traže inspiraciju u filozofiji.', '/assets/images/books/apology.jpeg', 30),
('Metamorfoze', '2024-01-20', 6, 17.99, 350, 'Latinski', 'Tvrdi uvez', 125, 200, 'Ovo remek-djelo antičke književnosti donosi fascinantan prikaz mitoloških priča i transformacija, stvarajući jedinstveno tkivo bajki i legendi. Autor, Publije Ovidije Nazon, prenosi nam šarolik svijet bogova, heroja i čudovišta kroz svoje pjesničko perje. Kroz svoje "Metamorfoze", Ovidije plete priče o ljubavi, tragediji i magiji, gdje se likovi preobražavaju i suočavaju s izazovima sudbine. Ova iznimna zbirka mitoloških priča oživljava antički svijet na način koji čitatelje uvlači u čudesno putovanje kroz epohe i likove, čineći je nezaobilaznim blagom za ljubitelje klasične književnosti.', '/assets/images/books/metamorphosis.jpeg', 30),
('Ispovijesti', '2024-02-22', 7, 15.99, 340, 'Latinski', 'Tvrdi uvez', 135, 215, 'Ispovijesti autora Augustina predstavljaju duboko introspektivno putovanje kroz život jednog od najznačajnijih crkvenih otaca. U ovoj jedinstvenoj autobiografiji, Augustin otvoreno dijeli svoje unutarnje dileme, borbe i traženje smisla. Kroz iskrene ispovijesti, čitatelji ulaze u kompleksnost ljudske duše, istražujući teme vjere, grijeha, i traženja božanske milosti. Augustinova introspekcija nudi dubok uvid u ljudsku prirodu, donoseći jedinstvenu kombinaciju teoloških razmatranja i emotivne iskrenosti. Ispovijesti ostaju relevantne i inspirativne, potičući čitatelje na razmišljanje o vlastitim životima i traženju duhovnog ispunjenja.', '/assets/images/books/confessions.jpeg', 30),
('Božanstvena komedija', '2024-02-02', 8, 35.99, 853, 'Talijanski', 'Tvrdi uvez', 150, 235, 'Ovo remek-djelo Dantea Alighierija vodi čitatelja na nevjerojatno putovanje kroz tri sfere: pakao, čistilište i raj. Glavni junak, vođen duševnim vodičem, prolazi kroz različite pejzaže i susreće raznovrsne likove koji predstavljaju ljudske strasti, grijehe i vrline. Danteova vizija nudi duboke simbole i alegorije, stvarajući kompleksnu priču o duhovnom iskupljenju, božanskoj pravdi i putovanju prema istinskom duhovnom znanju. Ova epska poema oslikava ljudsku sudbinu, postavljajući pitanja o moralu, odgovornosti i vječnosti. Danteova Božanstvena komedija ostavlja trajni dojam, ne samo zbog svoje književne veličine, već i zbog svoje duboke teološke i filozofske dubine. To je nezaobilazno štivo za one koji traže putokaz kroz zamršene staze ljudskog duha.', '/assets/images/books/divinecomedy.jpeg', 30),
('Dekameron', '2024-02-05', 9, 26.99, 892, 'Talijanski', 'Meki uvez', 125, 200, 'Ova zbirka priča Giovannia Boccaccia oživljava živopisnu sliku srednjovjekovne Italije kroz deset dana raznolikih priča ispričanih od strane deset mladih ljudi koji su pobjegli od kuge koja je poharala Firencu. Svaka priča je poput slikovitog prozorskog okvira, pružajući uvid u različite aspekte ljudske prirode, ljubavi, moralnosti i društvenih običaja tog vremena. Boccacciovo remek-djelo, prepuno humora, sarkazma i ljudske mudrosti, otkriva duboke istine kroz različite likove i njihove životne situacije. Kroz Dekameron, čitatelji putuju kroz šarolik spektar emocija, susrećući se s ljubavnim intrigama, humorom, ali i suzama, stvarajući jedinstvenu književnu fresku renesansne Italije. Ova knjiga ostaje neizostavno štivo za one koji žele uroniti u bogatstvo ljudskih priča i doživjeti svijet Boccacciovog genija.', '/assets/images/books/decameron.jpeg', 30),
('Kanconijer', '2024-02-14', 10, 19.99, 453, 'Talijanski', 'Meki uvez', 120, 190, 'Ova zbirka lirskih ljubavnih pjesama autora Petrarce, poznata kao Kanconijer, predstavlja vrhunac srednjovjekovne talijanske lirike. Petrarcine stihove odlikuje duboka emotivnost, gdje se autor izražava kroz introspektivna promišljanja o ljubavi, tuzi i prolaznosti vremena. Svaka pjesma je poput dragocjenog bisera, prenoseći čitatelju intimne trenutke zaljubljenosti, čežnje i ljubavnog uzdizanja. Petrarca, majstor soneta, kroz Kanconijer stvara poetsko remek-djelo koje istražuje složene nijanse ljudskih osjećaja. Zbirka ostavlja dubok dojam svojom sposobnošću da uhvati esenciju ljubavi i čitatelje vodi kroz labirint strasti. Za ljubitelje klasične poezije, Kanconijer nudi nezaboravno putovanje kroz raskoš Petrarcinih sentimenta.', '/assets/images/books/canzoniere.jpg', 30),
('Dundo Maroje', '2024-03-01', 11, 11.99, 139, 'Hrvatski', 'Tvrdi uvez', 115, 185, 'Ovo remek-djelo hrvatskog renesansnog dramatičara Marina Držića predstavlja iznimnu komediju koja vješto kombinira humor i satiru kako bi istražila društvene norme i ljudske slabosti. Radnja prati živopisnog protagonista, Dunda Maroja, koji se suočava s različitim izazovima života u Dubrovniku 16. stoljeća. Kroz živahne dijaloge, karikature likova te humoristične situacije, Držić stvara jedinstveno kazališno iskustvo koje istovremeno zabavlja i potiče razmišljanje. Ova književna poslastica donosi ne samo zadovoljstvo čitateljima ljubiteljima klasične komedije, već i uvid u živopisni svijet renesansne Dubrovačke republike.', '/assets/images/books/dundomaroje.jpeg', 30),
('Novela od Stanca', '2024-02-25', 11, 8.99, 65, 'Hrvatski', 'Meki uvez', 110, 170, 'Uzbudljiva komična avantura Novela od Stanca autora Marina Držića pruža jedinstven uvid u svijet dubrovačkih plemića tijekom karnevalskih veselja. Mladi plemići Vlaho i Miho, uz pomoć prijatelja Dživa, smišljaju prevaru nad naivnim Stancom. Uz zamke, nesporazume i neočekivane obrate, ova šaljiva priča o prepoznavanju, prerušavanju i prevarama pruža izuzetno duhovito i zabavno kazališno iskustvo. Novela od Stanca odiše originalnošću i šarmom dubrovačke renesansne scene, čineći je neodoljivom čitateljima željnima klasične komedije.', '/assets/images/books/novelaodstanca.jpeg', 30),
('Judita', '2024-01-24', 12, 12.99, 150, 'Hrvatski', 'Tvrdi uvez', 115, 185, 'Ovo remek-djelo Marka Marulića, jednog od najvažnijih hrvatskih renesansnih književnika, predstavlja epsku pjesničku sagu koja duboko zaranja u biblijsku priču o Juditi. Kroz raskošan jezik i pjesničku maštovitost, Marulić stvara živopisni portret hrabre junakinje koja se suočava s moralnim izazovima i donosi odlučujući čin hrabrosti. Djelo donosi bogatstvo kulturne i duhovne baštine, reflektirajući humanističke ideje renesanse. Marulićeva Judita ne samo da očarava svojim pjesničkim izričajem, već i postavlja duboka pitanja o vjeri, ljubavi i snazi ženskog karaktera. Ova epska poema čini izvrsno štivo za sve koji cijene klasičnu književnost i žele uroniti u slikoviti svijet Marulićevog stvaralaštva.', '/assets/images/books/judita.jpeg', 30),
('Planine', '2024-02-08', 13, 13.49, 162, 'Hrvatski', 'Tvrdi uvez', 115, 185, 'Ova iznimna književna poslastica, prvi hrvatski roman nazvan Planine, autora Petra Zoranića, pruža nezaboravno putovanje kroz pastirsku idilu, alegoriju i duhovnu odiseju. Kroz prozu i stih, Zoranić vješto prepliće klasične motive, talijanske utjecaje te domaći pečat, stvarajući remek-djelo koje se izdvaja kao jedinstveno u hrvatskoj književnosti renesansnog razdoblja. Ovo djelo čarobno spaja klasičnu književnost s domaćim utjecajima, stvarajući prožimajuću priču koja će zasigurno oduševiti ljubitelje klasičnih književnih remek-djela. Osvježite svoju knjižničnu kolekciju ovim jedinstvenim putovanjem kroz Zoranićevu pastoralnu viziju.', '/assets/images/books/planine.jpeg', 30),
('Romeo i Julija', '2024-03-12', 14, 13.99, 159, 'Engleski', 'Tvrdi uvez', 125, 200, 'Ovaj klasik svjetske književnosti, remek-djelo Williama Shakespearea, pruža nezaboravnu priču o ljubavi, strasti i nesreći. Kroz dramu ljubavi između mladih Romea i Julije, Shakespeare stvara djelo koje je istodobno pogođeno dubokim emotivnim nabojem i suptilnim lirskim izrazom. Radnja se odvija u Veroni, gdje dvije obitelji, Montecchi i Capuleti, gaje dugu mržnju. No, ljubav između Romea i Julije, pripadnika suprotstavljenih obitelji, postaje simbol borbe protiv okrutnosti sudbine. Shakespearova spretnost u izražavanju ljudskih emocija i dubine ljubavi čini ovu tragediju neizbrisivom u svjetskoj književnosti. Ovo djelo poziva čitatelje na emocionalno putovanje kroz zamršene odnose, sukobe obitelji i snagu prave ljubavi koja će osvojiti srca ljubitelja klasične književnosti i pružiti nezaboravan doživljaj čarolije Shakespearovog pera.', '/assets/images/books/romeoandjuliet.jpeg', 30),
('San Ivanjske noći', '2024-03-12', 14, 9.99, 85, 'Engleski', 'Meki uvez', 130, 205, 'Ovo remek-djelo Williama Shakespearea, čarobna komedija San Ivanjske noći, donosi jedinstvenu mješavinu ljubavi, intriga i magije. Radnja se odvija u šumskom krajoliku tijekom jedne vrele ljetne noći, gdje se sudbine ljubavnika isprepliću na nezaboravan način. Shakespeare vješto plete priču o zapletima među ljubavnim parovima, uz dodatak čarolije vila i vilenjaka. San Ivanjske noći oduševljava svojom inventivnošću, bogatstvom likova i šarmom Shakespearovog jezika. Ovo je djelo koje će osvojiti srca ljubitelja klasične književnosti, pružajući čitateljima nezaboravan doživljaj ljubavi i magije pod zvijezdanim nebom.', '/assets/images/books/midsummernight.jpeg', 30),
('Hamlet', '2024-02-10', 14, 14.49, 189, 'Engleski', 'Tvrdi uvez', 140, 220, 'Ovo remek-djelo Williama Shakespearea, u kojem se isprepliću tragedija, tajne i moralne dileme, predstavlja jedno od najvažnijih djela svjetske književnosti. Radnja prati mladog princa u njegovoj borbi protiv korupcije, izdaje i unutarnjih sukoba. Shakespeare majstorski istražuje dubine ljudske psihe kroz lik Hamleta, čije se unutarnje previranje odražava kroz nezaboravne monologe. Priča o osveti, gubitku i moralnoj složenosti oduzima dah svojom kompleksnošću i dubinom. Ova drama pruža nezaboravan doživljaj čitateljima koji traže izazovne priče, snažne likove i razmišljanje o ljudskoj prirodi.', '/assets/images/books/hamlet.jpeg', 30),
('Kralj Lear', '2024-03-06', 14, 10.49, 115, 'Engleski', 'Meki uvez', 130, 205, 'Ovo klasično Shakespearovo djelo predstavlja epsku tragediju koja istražuje ljudsku sudbinu, vlast i obiteljske odnose. Radnja prati kralja koji, suočen s teškim odlukama, donosi pogrešne izbore, što pokreće lavinu tragedija. Djelo oduzima dah svojom emotivnom dubinom, sukobima među likovima i snažnom slikom ljudske slabosti. Shakespeare, majstor psihologije likova, stvara nezaboravne likove poput Leara, Cordelie i Edgara, čije priče ostavljaju snažan dojam na čitatelje. Ova drama je nezaobilazno štivo za ljubitelje klasične književnosti, nudeći izuzetnu meditaciju o moći, moralu i tragičnosti ljudskog iskustva.', '/assets/images/books/kinglear.jpeg', 30),
('Oluja', '2024-03-07', 14, 8.49, 89, 'Engleski', 'Meki uvez', 130, 205, 'Ova Shakespeareova drama, poznata po svojoj složenoj radnji, mitskim elementima i dubokim ljudskim emocijama, istražuje teme izdaje, pomirenja i obnove. Smještena na otoku, radnja prati likove koji se suočavaju s intrigama, magijom i osobnim transformacijama. Oluja je remek-djelo koje spaja fantastične elemente s dubokim ljudskim iskustvima. Ova drama nudi čitateljima bogato iskustvo koje istražuje ljubav, obitelj i moć. Uživajte u Shakespearovoj genijalnosti kroz ovu intrigantnu priču o preobražaju i pomirenju.', '/assets/images/books/tempest.jpeg', 30),
('Otelo', '2024-02-28', 14, 9.99, 113, 'Engleski', 'Meki uvez', 130, 205, 'U ovoj epskoj Shakespearovoj tragediji, zapleteni smo u priču o strastvenoj ljubavi, prijevari i tragičnim posljedicama. Otelo, hrabri vojni vođa, postaje žrtva zavjere koja podmuklo uništava njegovu ljubavnu vezu s nevinom i vjerom obdarenom Desdemonom. Oluja strasti, spletki i manipulacija pod vodstvom zavidnog Jaga prikazuje mrak ljudske duše. Shakespeare kroz likove Otela, Jaga i Desdemone otvara vrata dubokim emocionalnim sukobima, pružajući nam uvid u dubine ljudske kompleksnosti. Otelo je remek-djelo koje nas suočava s moralnim pitanjima, emocionalnim vrtlozima i gorkim okusom izdaje. Ova drama ne samo da prožima srce, već i potiče na razmišljanje o prirodi ljudske sudbine.', '/assets/images/books/othello.jpeg', 30),
('Don Quijote', '2024-01-06', 15, 19.99, 862, 'Španjolski', 'Tvrdi uvez', 150, 235, 'Ovo iznimno djelo Miguela de Cervantesa prati neobične pustolovine viteza i njegova vjernog prijatelja kroz šareni svijet fantastičnih situacija. Smješten u španjolskom ruralnom krajoliku, roman istražuje sukob između stvarnosti i mašte, donoseći duhovitu satiru o viteškim idealima. S Cervantesovim vještim pripovijedanjem, čitatelji će se susresti s jedinstvenim likovima, neočekivanim preokretima i dubokim razmišljanjima o prirodi ljudske stvarnosti. Uronite u ovu klasičnu priču koja će vas nasmijati, ali i potaknuti na promišljanje o granicama između snova i stvarnosti.', '/assets/images/books/donquijote.jpeg', 30),
('Osman', '2024-02-02', 16, 13.99, 161, 'Hrvatski', 'Tvrdi uvez', 140, 220, 'Ovo epohalno djelo Ivana Gundulića, smješteno u stoljeće borbi između Osmanskog Carstva i Mletačke Republike, predstavlja dramatičnu pjesničku viziju sukoba između kršćanske i muslimanske civilizacije. Gundulićeva "Osman" otkriva sudbinu vojnog lidera Osmana II., dok istražuje teme vlasti, hrabrosti i moralnih izazova pred ratom. S bogatim jezikom i dubokim emotivnim tonovima, ovo remek-djelo ostavlja snažan dojam na čitatelje, otvarajući prozor u svijet sukoba i dilema turskog vladara.', '/assets/images/books/osman.jpeg', 30),
('Suze sina razmetnoga', '2024-03-04', 16, 8.99, 70, 'Hrvatski', 'Meki uvez', 130, 205, 'Uživajte u dubokom duhovnom putovanju kroz remek-djelo Ivana Gundulića, poemu koja prožima starozavjetnu temu iz Lukina Evanđelja. Suze sina razmetnoga donose proširenu priču o izgubljenom sinu, bogato oblikovanu kroz monologe koji istražuju dubine ljudske duše. Ova poema ne samo da produbljuje karakterizaciju glavnog lika, već se i duboko uranja u teme kajanja, oprosta i duhovnog pročišćenja. Inspirirana Biblijskom parabolom, Gundulićeva interpretacija ističe grijeh, posebno bludnost, kao ključni element koji pokreće radnju i potiče na duboka promišljanja o moralnim pitanjima. Suze sina razmetnoga obećavaju ne samo čitateljsko iskustvo, već i uvid u ljudsku prirodu i vrednote koje nadilaze vlastiti put kroz vjeru, kajanje i oprost.', '/assets/images/books/prodigalson.jpeg', 30),
('Dubravka', '2024-02-27', 16, 9.99, 95, 'Hrvatski', 'Meki uvez', 135, 215, 'Dubravka, Gundulićeva epska drama kroz dubrovačku prizmu istražuje ljubav, političke spletke i borbu za slobodu. Smještena u kontekst izmišljenog običaja posvećenog božici slobode, priča prati sudbinu mladog para, Dubravke i Miljenka, suočenog s izazovima političke korupcije. Ključni trenutak drame je neočekivani izbor ružnog bogataša Grdana umjesto očekivanog para za proslavu blagdana slobode, što rezultira nadnaravnim odgovorom boga Lera. U završnom činu, svećenik prinosi simbolične darove ljubavi i slobode, dok likovi simbolizirani društvenim manama predaju svoje poroke kao zavjetni dar u čast slobode Dubrovnika. Ova epska drama oslikava duh vremena kroz intrige, ljubavne zaplete i moralne izbore, čineći je neizostavnom čitankom za ljubitelje dubrovačke kulture i književnosti.', '/assets/images/books/dubravka.jpeg', 30)